import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppAuth } from '../lib/auth';

const StartPage: React.FC = () => {
  const navigate = useNavigate();
  const { signOut } = useAppAuth();

  // Development mode detection
  const isDevelopment = window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1';

  const handleBeginCleanse = () => {
    if (isDevelopment) {
      // In development mode, use mock sign in
      (window as any).mockSignIn = () => {
        const mockState = (window as any).mockAuthState || {
          isSignedIn: false,
          userId: null,
          user: null
        };
        mockState.isSignedIn = true;
        mockState.userId = 'dev-user-123';
        mockState.user = {
          firstName: 'Dev',
          lastName: 'User',
          primaryEmailAddress: { emailAddress: 'dev@localhost.com' }
        };
        window.location.href = '/';
      };
      (window as any).mockSignIn();
    } else {
      // In production, navigate to the dashboard (Clerk will handle auth)
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-lavender-starburst px-4">
      <div className="max-w-md w-full text-center">
        <div className="flex flex-col items-center mb-8">
          <span className="text-5xl font-extrabold text-lavender-700 tracking-tight flex items-center justify-center">
            Paratox
            <span className="ml-2 text-yellow-400 text-3xl">⭐️</span>
          </span>
          <p className="mt-2 text-lg text-lavender-600 font-medium">Remove toxins. Reclaim your health.</p>
        </div>
        <div className="bg-white/80 dark:bg-gray-900/80 rounded-2xl shadow-lg p-6 mb-8">
          <p className="text-gray-700 dark:text-gray-200 text-base mb-4">
            Paratox is your companion for a guided 28-day parasite cleanse.<br/>
            Track your daily progress, log biofeedback, and follow a science-backed protocol to eliminate toxins and restore your vitality.<br/>
            Start your journey to a healthier, more energized you—one day at a time.
          </p>
          <button
            className="mt-4 px-6 py-3 rounded-xl bg-lavender-600 hover:bg-lavender-700 text-white font-bold text-lg shadow transition"
            onClick={handleBeginCleanse}
          >
            Begin Your Cleanse
          </button>
        </div>
      </div>
    </div>
  );
};

export default StartPage;
